import { useEffect, useState } from "react";
import "../styles/projectpage.css";

function ProjectPage({ myEmail }) {
  const [data, setData] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  // skill input value
  let[skillName, setSkillName] = useState('');
  let[skillDescription, setSkillDescription] = useState('');

  useEffect(() => {
    setIsLoading(true);
    fetch(`https://phase3-project.onrender.com/user/${myEmail}`)
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        console.log(data["skills"]);
        setIsLoading(true);
      })
      .catch((error) => {
        console.log(error);
        setIsLoading(true);
      });
  }, [myEmail]);

  // user details
  let name = data["first_name"] + " " + data["last_name"];
  let bio = data["bio"];
  let career = data["career"];
  let skillsList = data["skills"];
  let projectsList = data["projects"];
  let id = data["id"];

  console.log(id)

  console.log(skillsList);


  let handleUpdating =(name, description, skill_id)=>{
    let newObj = {
    name,
    description
    }

    console.log(newObj);
    fetch(`https://phase3-project.onrender.com/skills/${id}/${skill_id}`, {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newObj),
    })
    .then(response => response.json())
    .then(data => {
      // handle success response
      console.log(data)
    })
    .catch(error => {
      // handle error response
      console.log(error);
    });
  }

// deleting skills
let handleDeletingSkills = (skill_id) => {
  fetch(`https://phase3-project.onrender.com/destroy/skills/${id}/${skill_id}`, {
  method: 'DELETE',
  headers: {
    'Content-Type': 'application/json'
  }
})
.then(response => {
  if (!response.ok) {
    throw new Error('Network response was not ok');
  }
  return response.json();
})
.then(data => {
  console.log(data);
})
.catch(error => {
  console.error('There was a problem with the fetch operation:', error);
});
